import { Formik } from 'formik';
import React, { useEffect, useState } from 'react'
import { toast } from 'react-hot-toast';
import { useNavigate, useParams } from 'react-router-dom';

const Update_Pro = () => {

    const { id } = useParams();
    // console.log(id);
    const [ProfileData, setProfileData] = useState(null)
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate();

    const getProfileByid = async () => {
        setLoading(true);
        const res = await fetch('http://localhost:5000/profile/getbyid/' + id);
        console.log(res.status);

        if (res.status === 200) {
            const data = await res.json();
            console.log(data);
            setProfileData(data);
            setLoading(false);
        }
    }

    useEffect(() => {
        getProfileByid();
    }, []);

    const updated_profile_Submit = async (formdata) => {
        console.log(formdata)

        const res = await fetch('http://localhost:5000/profile/update/' + id, {
            method: 'PUT',
            body: JSON.stringify(formdata),
            headers: { "Content-Type": "application/json" },
        })

        console.log(res.status);
        if (res.status === 200) {
            toast.success('User Updated Successfully');
            // navigate('/manageuser')
        }


    }





    const showUpdateForm = () => {
        if (!loading && ProfileData) {
            return (
                <div className='card mt-5'>
                    <div className="card-body">
                        <h1 className="text-center">Signup</h1>

                        <Formik
                            initialValues={ProfileData}
                            onSubmit={updated_profile_Submit} >

                            {
                                ({ values, handleChange, handleSubmit }) => (
                                    <form onSubmit={handleSubmit}>
                                        <label>CompanName</label>
                                        <input required type="name" className='form-control mb-3' name="name" onChange={handleChange} value={values.name} />

                                        <label>Type</label>
                                        <input required type="type" className='form-control mb-3' name="type" onChange={handleChange} value={values.type} />

                                        <label>Username</label>
                                        <input required type="text" className='form-control mb-3' name="username" onChange={handleChange} value={values.username} />

                                        <label>Age</label>
                                        <input required type="number" className='form-control mb-3' name="age" onChange={handleChange} value={values.age} />

                                        <button type="submit" className="btn btn-primary w-100">Submit</button>
                                    </form>
                                )
                            }
                        </Formik>

                    </div>
                </div>
            )
        }
    }





    return (
        <div>

            <div className="container">
                {showUpdateForm()}
            </div>


        </div>
    )
}

export default Update_Pro
